import { makeAutoObservable } from 'mobx';

class ProductStore {
  
  products = [];
  selectedProduct = {
    title: null,
    description: null,
    price: null,
    rating: null,
    brand: null,
    images: null, 
  };

  constructor() {
    makeAutoObservable(this);
  }

  setProducts(products) {
    this.products = products;
  }

  setSelectedProduct(product) {
    this.selectedProduct.title = product.title;
    this.selectedProduct.description = product.description;
    this.selectedProduct.price = product.price;
    this.selectedProduct.rating = product.rating;
    this.selectedProduct.brand = product.brand;
    this.selectedProduct.images = product.images;
    console.log("Selected product changed to: ", product.title);
  }
}

const productStore = new ProductStore();



export default productStore;
