import React, { useEffect } from 'react';
import { observer } from 'mobx-react-lite';
import ProductCard from '../Components/ProductCard';
import productStore from '../Stores/ProductStore';
import './ProductsPage.css'

const ProductsPage = () => {

  useEffect(() => {
    const fetchProducts = async () => {
      const response = await fetch('https://dummyjson.com/products');
      const products = await response.json();
      productStore.setProducts(products.products);
    };
  
    fetchProducts();
  }, []);


  return (
    <div >
      { productStore.products.map((product, index) => {
          if (index % 3 === 0) {
            return (
              <div key={product.id} className="product-card-group">
                <ProductCard product={product} className="product-card" />
                <ProductCard
                  product={productStore.products[index + 1]}
                  className="product-card"
                />
                <ProductCard
                  product={productStore.products[index + 2]}
                  className="product-card"
                />
              </div>
            );
          }
          return null;
        })}
    </div>
  );
};

export default observer(ProductsPage);
