import React from 'react';
import { observer } from 'mobx-react-lite';
import productStore from '../Stores/ProductStore';
import { toJS } from 'mobx';
import './ProductDetails.css'

const ProductDetails = () => {

  console.log(toJS (productStore.selectedProduct));
  return (
    <div>
      <div className="container">
        <h2>{productStore.selectedProduct.title} </h2>
        <h4>{productStore.selectedProduct.descpription} </h4>  
        <ul>
          <img src={productStore.selectedProduct.images[1]} width='100%' alt={productStore.selectedProduct.title}/>
          <li>
            {productStore.selectedProduct.title}
          </li>
          <li>
            {productStore.selectedProduct.title}
          </li>
        </ul>
      </div>
    </div>
  );
};

export default observer(ProductDetails);
