import React, { useEffect } from 'react';
import { observer } from 'mobx-react-lite';
import ProductCard from '../Components/ProductCard';
import productStore from '../Stores/ProductStore';

const ProductsPage = () => {

  useEffect(() => {
    // Simulated API call to fetch products
    const fetchProducts = async () => {
      const response = await fetch('https://dummyjson.com/products');
      const products = await response.json();
      productStore.setProducts(products.products);
    };
  
    fetchProducts();
  }, []);

  const handleProductClick = (product) => {
    productStore.setSelectedProduct(product);
  };

  return (
    <div className="main-page">
      {productStore.products.length > 0 && productStore.products.map((product) => (
        <ProductCard key={product.id} product={product} onClick={handleProductClick} className="product-card"/>
      ))}
    </div>
  );
};

export default observer(ProductsPage);
